import styled from 'styled-components';

export const StyledCart = styled.div`
    display: flex;
    margin: 0 5rem;
`;

export const StyledHr = styled.div`
    background-color:#fff ;
    border-top: 2px dashed ${props => props.theme.colors.primary};
`;

export const StyledInfo = styled.div`
    flex: 0 0 60%;
    padding: 1rem;
    border-radius: 8px;
    box-shadow: 0 1px 5px rgba(0,0,0,.2);
    background-color: #fff;
`;

export const Tag = styled.div`
    text-transform: uppercase;
    font-size: 1.6rem;
`;

export const Description = styled.p`
    font-size: 1.6rem;
`;

export const Cost = styled.p`
    justify-self: right;
    font-size: 1.6rem;
`;

export const Total = styled.p`
    justify-self: right;
    font-size: 1.6rem;
`;

export const Quantity = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    justify-self: end;

    button {
        background-color: #fff;
        border-radius: 50%;
        width: 3rem;
        height: 3rem;
        outline: none;
        cursor: pointer;
    }
    p {
        text-align: center;
        width: 3rem;
        line-height: 3rem;
    }

`;


export const InfoFrame = styled.div`

    display: grid;
    grid-template-columns: repeat(5,1fr);

    & > * {
        justify-self: center;
        align-self: center;
    }
    img {
        justify-self: center;
        padding: 1rem;
        width: 90%;
        grid-column: 1/2;
        grid-row: 1/span 2;
    }

    ${Tag} {
        grid-column: 2/3;
        grid-row: 1;
    } 

    ${Description} {
        grid-column: 2/4;
        grid-row: 2;
    }

    ${Cost} {
        grid-column: 3/4;
        grid-row: 1;
    }

    ${Quantity}{
        grid-column: 4/5;
        grid-row: 1;
    }

    ${Total}{
        grid-column: 5/6;
        grid-row: 1;
    }



`;

export const TableHeading = styled.div`
    display: grid;
    grid-template-columns: repeat(5,1fr);
    text-align: right;
    text-transform: uppercase;
    font-size: 1.6rem;
    font-weight: 800;
    padding: 1rem;

    p {
        font-size: 1.6rem;
        justify-self: center;
    }
    & p:nth-child(2) {
        grid-column: 3 / 4;
    }

`;

export const StyledPrice = styled.div`
    flex: 0 0 40%;
    display: flex;
    flex-direction: column;
`;
