import React from 'react'
import { StyledCart, StyledInfo, TableHeading, CheckoutForm } from './StyledCart'
import Product from './Product';
import OrderCheckout from './OrderCheckout'
import { useSelector, useDispatch } from 'react-redux';
import PopupForm from './PopupForm';




const getCart = (state) => state.cart

function Cart() {

    const [showPopup, setShowPopup] = React.useState('false');

    const cart = useSelector(getCart);
    const keys = Object.keys(cart.products);
    const { items, total } = cart




    // const makeAlert = React.useCallback(() => {

    //     if (message) {
    //         if (isSubmitting === true) {
    //             alert(message);
    //         }
    //     }
    // }, [message, isSubmitting]);


    return (
        <StyledCart>
            <StyledInfo>
                <TableHeading>
                    <p>Product</p>
                    <p>Price</p>
                    <p>Quantity</p>
                    <p>Total</p>
                </TableHeading>
                {keys.map((productId, index) => {
                    return <Product key={index} product={cart.products[productId]} productId={productId} />
                })}
            </StyledInfo>
            <OrderCheckout data={{ items, total }} />
            <PopupForm />

        </StyledCart>
    )
}

export default Cart
