import styled, { keyframes } from 'styled-components';
import pizza from "../../assets/pizza.png";

const rotate = keyframes`
  from {
    transform: translate(-50%, -50%) scale(1.5);
  }
  to {
    transform: translate(-125%, -45%) scale(1);
  }
`;

export const StyledHome = styled.div`
    position: relative;
    height: 100vh;
    text-align: center;    
    overflow: hidden;
    display: flex;
    flex-direction: column;
    align-items: flex-end;

`;


export const StyledBanner = styled.section`
    padding-top: 15vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    width: 50%;

    h1 {
        margin: 3rem auto;
        font-size: 5rem;
    }

    li {
        list-style: none;
        font-size: 2rem;

        :not(:first-child){
            margin-top: 1rem;
        }
    }
`;

export const ProductImage = styled.div`
    position: absolute;
    left: 50%;
    top: 50%;
    width: 50%;
    background: url(${pizza}) no-repeat center ;
    animation: ${rotate} 0s cubic-bezier(.83,-0.57,.72,1);
    animation-fill-mode: forwards;

    :after {
    content: "";
    display: block;
    padding-bottom: 100%;
}

`;

export const StyledProcess = styled.div`

  display: block;
  height: 10rem;
  width:  50rem;
  background-image: linear-gradient(90deg, red 0%, red 33%, yellow 33%, yellow 66% , green 66% , green 100%);

`;
