import React from 'react';
import { Dropdown, DropdownItem } from './Styled';

const routes = {
    unauthenticated: [
        {
            path: '/auth/signin',
            content: 'Sign In',
        },
        {
            path: '/auth/signup',
            content: 'Sign Up',
        }],
    authenticated: [
        {
            path: '/auth/profile',
            content: 'Profile',
        },
        {
            path: '/auth/logout',
            content: 'Logout',
        }]
}
const status = 'authenticated';

const DropdownMenu = () => {

    return (
        <Dropdown>
            {routes[status].map((route, index) => {
                return <DropdownItem key={index} to={route.path}>{route.content}</DropdownItem>
            })}
        </Dropdown>
    )
}

export default DropdownMenu;
