import styled, { keyframes } from 'styled-components';
import pizza from "../../assets/pizza.png";

export const StyledHome = styled.div`
    position: relative;
    height: 100vh;
    text-align: center;    
    overflow: hidden;

`;

export const DetailBox = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 5rem;
`;

export const StyledHr = styled.hr`
  border: none;
  display: block;
  height: .5rem;
  width:  50rem;
  background-image: linear-gradient(90deg, red 0%, red 33%, yellow 33%, yellow 66% , green 66% , green 100%);

`;

export const StyledBanner = styled.section`
    padding-top: 15vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    width: 50%;
    margin-bottom: 2rem;

    h1 {

        margin: 3rem auto;
        font-size: 5rem;
    }
    ul {
      margin-top: 2rem;
    }
    li {
        list-style: none;
        font-size: 2rem;

        :not(:first-child){
            margin-top: 1rem;
        }
    }
`;

export const ProductImage = styled.div`
    position: absolute;
    left: 50%;
    top: 50%;
    width: 50%;
    height: 100vh;
    background: url(${pizza}) no-repeat center;
    transform: translate(-125%, -45%);

`;

export const StyledProcess = styled.div`

  display: block;
  height: 10rem;
  width:  50rem;
  border: 2px solid #000;

`;
