import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import UserServices from '../../service/UserServices';
import { useDispatch } from 'react-redux';

export const authUserbySignup = createAsyncThunk(
    'auth/authUserbySignupStatus',
    async (info, { getState, requestId }) => {
        const { currentRequestId, authenticating } = getState().auth;
        if (authenticating === true || requestId !== currentRequestId) {
            return
        }
        const data = await UserServices.signup(info);
        return { ...data, username: info.username };
    }
)

export const authSlice = createSlice({
    name: 'auth',
    initialState: {
        currentUser: { username: '' },
        authenticating: false,
        authenticated: false,
        token: '',
        error: ''
    },
    reducers: {
        request: (state, action) => {
            state.authenticating = true;
        },
        success: (state, action) => {
            state.currentUser = action.payload.username;
            state.authenticated = true;
            state.authenticating = false;
            state.token = action.payload.tooken;
        },
        error: (state, action) => {
            state.authenticating = false;
            state.error = action.payload.error;
        }
    },
    extraReducers: {
        [authUserbySignup.pending]: (state, action) => {
            console.log(action);
            if (state.authenticating === false) {
                state.authenticating = true;
            }
        },
        [authUserbySignup.fulfilled]: (state, action) => {
            const { msg, username } = action.payload;
        },
    }
});


const { actions, reducer } = authSlice;

export const { request, success, error } = actions;

export default reducer;
