import React from 'react'
import { StyledCart, StyledInfo, TableHeading, CheckoutForm } from './StyledCart'
import Product from './Product';
import OrderCheckout from './OrderCheckout'
import { useSelector } from 'react-redux';
import Alert from '../sharecomponents/Alert';


const getCart = (state) => state.cart
function Cart() {

    const cart = useSelector(getCart);
    const keys = Object.keys(cart.products);
    const { items, total } = cart

    return (
        <StyledCart>
            <StyledInfo>
                <TableHeading>
                    <p>Product</p>
                    <p>Price</p>
                    <p>Quantity</p>
                    <p>Total</p>
                </TableHeading>
                {keys.map((productId, index) => {
                    return <Product key={index} product={cart.products[productId]} productId={productId} />
                })}
            </StyledInfo>
            <OrderCheckout data={{ items, total }} />

            <CheckoutForm>
                <Alert />
                <form action="#">
                    <label htmlFor="address"></label>
                    <input type="text" id="address" name="address" />
                </form>
            </CheckoutForm>
        </StyledCart>
    )
}

export default Cart
