import styled from 'styled-components';

export const HomeWrapper = styled.div`
    display: flex;
    flex-direction: column;
`;

export const StyledBanner = styled.section`
    display: flex;
    flex-direction: column;
    width: 100%;
    align-items: center;
    text-align: center;

    h1 {
        font-size: 5rem;
        margin: 5rem;
    }

    li {
        list-style: none;
        font-size: 2rem;

        :not(:first-child){
            margin-top: 1rem;

        }
    }
`;
