import React from 'react'
import { StyledCart, StyledInfo, TableHeading } from './StyledCart'
import Product from './Product';
import Price from './Price'
import { useSelector } from 'react-redux';


const getCart = (state) => state.cart.products
function Cart() {

    const cart = useSelector(getCart);
    const keys = Object.keys(cart);
    let items = 0, total = 0;

    return (
        <StyledCart>
            <StyledInfo>
                <TableHeading>
                    <p>Product</p>
                    <p>Price</p>
                    <p>Quantity</p>
                    <p>Total</p>
                </TableHeading>
                {keys.map((productId, index) => {
                    items += cart[productId].quantity;
                    total += cart[productId].quantity * cart[productId].price;
                    return <Product key={index} product={cart[productId]} productId={productId} />
                })}
            </StyledInfo>
            <Price data={{ items, total }} />
        </StyledCart>
    )
}

export default Cart
