import React, { useContext, useState, createContext } from 'react';
import PropTypes from 'prop-types';

export const HeaderNavContext = createContext();
export const useHeaderNavContext = () => useContext(HeaderNavContext);

export const HeaderValueProvider = ({ children } = () => { }) => {
    const [page, setPage] = useState('Home');

    const handleClick = (e) => {
        console.log(e.target.value);
        setPage(e.target.value);
    }

    return (
        <HeaderNavContext.Provider values={{ page, handleClick }}>
            {children}
        </HeaderNavContext.Provider>
    )
}


HeaderValueProvider.propTypes = {
    children: PropTypes.node,
}
HeaderValueProvider.defaultProps = {
    children: () => { }
}
