import React from 'react';
import './App.css';
import Header from './components/layout/Header/Header';
import Menu from './components/layout/Content/Menu/Menu';
import styled from 'styled-components';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch
} from "react-router-dom";
import SideBar from './components/layout/Content/SideBar/SideBar';
import routes from './components/layout/Content/SideBar/routes';

const ContentWrapper = styled.div`
  display: flex;
`;

function App() {
  let { path, url } = useRouteMatch();
  console.log(path, url);
  return (
    <Router>
      <Header></Header>

      <Switch>
        <ContentWrapper>
          <Route path="/:page">
            <SideBar>
            </SideBar>
            {routes.map((route, index) => {
              return <Link key={index} to={route.path}>
                <div >{route.content}
                </div>
              </Link>
            })}
          </Route>
        </ContentWrapper>
      </Switch>

    </Router>
  );
}

export default App;
