import React from 'react';
import { StyledPrice, NavigationButton, CheckoutButton } from './StyledCart';

const OrderCheckout = ({ data, handleClick }) => {
    const { items, total } = data;
    return (
        <StyledPrice>
            <p>Order</p>
            <p>{items} items</p>
            <p>Total</p>
            <p>{`${new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(total)}`}</p>
            <NavigationButton to="/menu">Continue Orders</NavigationButton>
            <CheckoutButton onClick={handleClick}>Checkout</CheckoutButton>
        </StyledPrice>
    )
}

export default OrderCheckout;
