import { configureStore } from '@reduxjs/toolkit';
import authReducer from './auth/AuthSlice';
import cartReducer from './cart/CartSlice';

const rootStore = configureStore({
    reducer: {
        auth: authReducer,
        cart: cartReducer
    }
});

export default rootStore;
