import styled from 'styled-components';
import pizza from "../../assets/pizza.png";

export const StyledHome = styled.div`
    position: relative;
    padding-top: 8vh;
    height: 100vh;
`;


export const StyledBanner = styled.section`
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    width: 100%;





    h1 {
        margin: 3rem auto;
        font-size: 5rem;
    }

    li {
        list-style: none;
        font-size: 2rem;

        :not(:first-child){
            margin-top: 1rem;
        }
    }
`;

export const BackgroundImage = styled.div`

    position:  absolute;
    height: 100%;
    width: 100vw;
    top: 0;
    background: url(${pizza}) right center no-repeat;
    overflow: hidden;

`;
