import React from 'react';
import { FormContainer, FormTitle, InputField, Label, Input, Button, Wrapper } from './StyledAuth';
import {
    useFormik
} from 'formik';
import { useSelector, useDispatch } from 'react-redux';
import { authUserbySignup } from '../../redux/auth/AuthSlice';
import { validate } from './validator';
import { history } from '../../service/History';
const initialValues = {
    username: '',
    password: ''
};

const getAuthenticated = (state) => {
    return state.auth.authenticated
};


const SignIn = () => {


    const isAuthenticated = useSelector(getAuthenticated);

    const dispatch = useDispatch();

    const formik = useFormik({
        initialValues,
        onSubmit: async (values, { setSubmitting, resetForm }) => {

            setSubmitting(true);
            try {
                dispatch(authUserbySignup({ values, type: 'login' }));

            }
            catch (error) {

            }
            setSubmitting(false);
        },
        validate
    })

    if (isAuthenticated) {
        history.push('/home');
    }
    return (
        <Wrapper>
            <FormContainer>
                <FormTitle>Sign in</FormTitle>
                <form action="#">
                    <InputField>
                        <Label htmlFor="username">Username</Label>
                        <Input autoFocus type="text" id="name" name="name" />
                    </InputField>
                    <InputField>
                        <Label htmlFor="password">Password</Label>
                        <Input type="password" id="password" name="password" />
                    </InputField>
                    <Button>Sign in</Button>
                </form>
            </FormContainer>
        </Wrapper>
    )
}

export default SignIn;
