import React from 'react'
import { StyledCart, StyledInfo, TableHeading, CheckoutForm } from './StyledCart'
import Product from './Product';
import OrderCheckout from './OrderCheckout'
import { useSelector, useDispatch } from 'react-redux';
import Alert from '../sharecomponents/Alert';
import { useFormik } from 'formik';

const initialValues = {
    address: '',
};

const getAuthenticated = (state) => {
    return state.auth.authenticated;
};

const getCart = (state) => state.cart
function Cart() {

    const isAuthenticated = useSelector(getAuthenticated);

    const dispatch = useDispatch();

    const formik = useFormik({
        initialValues,
        onSubmit: async (values, { setSubmitting, resetForm }) => {

            setSubmitting(true);
            try {
                dispatch(resetMsg());
                await dispatch(authUser({ values, type: 'login' }));
            }
            catch (error) {

            }
            setSubmitting(false);
        },
        validate: (values) => {
            const errors = {};
            const { address } = values;

            if (!address) {
                errors.address = 'Required';
            }
            return errors;

        }
    })

    const cart = useSelector(getCart);
    const keys = Object.keys(cart.products);
    const { items, total } = cart

    return (
        <StyledCart>
            <StyledInfo>
                <TableHeading>
                    <p>Product</p>
                    <p>Price</p>
                    <p>Quantity</p>
                    <p>Total</p>
                </TableHeading>
                {keys.map((productId, index) => {
                    return <Product key={index} product={cart.products[productId]} productId={productId} />
                })}
            </StyledInfo>
            <OrderCheckout data={{ items, total }} />

            <CheckoutForm>
                <Alert />
                <form action="#">
                    <label htmlFor="address"></label>
                    <input type="text" id="address" name="address" />
                </form>
            </CheckoutForm>
        </StyledCart>
    )
}

export default Cart
