import styled from 'styled-components';

export const StyledCart = styled.div`
    display: flex;
`;

export const StyledInfo = styled.div`
    flex: 0 0 60%;
`;

export const Tag = styled.div`
    text-transform: uppercase;
    font-size: 1.6rem;
`;

export const Description = styled.p`
    font-size: 2rem;
`;

export const Cost = styled.p`
    font-size: 1.6rem;
`;

export const Total = styled.p`
    font-size: 1.6rem;
`;

export const Quantity = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    border: 1px solid #fff;

    button {
        width: 3rem;
        height: 3rem;
        outline: none;
        cursor: pointer;
    }
    p {
        text-align: center;
        width: 3rem;
        line-height: 3rem;
    }

`;


export const InfoFrame = styled.div`

    display: grid;
    grid-template-columns: repeat(5,1fr);

    img {
        padding: 1rem;
        justify-self: center;
        width: 90%;
        grid-column: 1/2;
        grid-row: 1/span 2;
    }

    ${Tag} {
        grid-column: 2/3;
        grid-row: 1;
    } 

    ${Description} {
        grid-column: 2/4;
        grid-row: 2;
    }

    ${Cost} {
        grid-column: 3/4;
        grid-row: 1;
    }

    ${Quantity}{
        grid-column: 4/5;
        grid-row: 1;
    }

    ${Total}{
        grid-column: 5/6;
        grid-row: 1;
    }



`;

export const TableHeading = styled.div`
    display: grid;
    grid-template-columns: repeat(5,1fr);
    text-align: right;

    p {
        font-size: 1.6rem;
    }
    & p:nth-child(2) {
        grid-column: 3 / 4;
    }

    & p:nth-child(1) {
       justify-self: center;
    }

`;

export const StyledPrice = styled.div`
    flex: 0 0 40%;
`;
