import React from 'react'
import {
    useParams,
    Route,
    Switch
} from 'react-router-dom';

import Cart from './Content/Cart/Cart';
import Home from './Content/Home/Home';
import Menu from './Content/Menu/Menu';

const routes = [
    {
        path: '/menu',
        component: Menu,
    },
    {
        path: '/home',
        component: Home,
    },
    {
        path: '/cart',
        component: Cart,
    }
]


function ContentRoutes() {
    let { page, content } = useParams();

    return (
        <Switch>
            {routes.map((route, index) => {

                return <Route path={`${route.path}/:content`}>
                    <route.component content={content} />
                </Route>
            })}
        </Switch>

    )
}

export default ContentRoutes
