    position: relative;
    height: 100vh;
    /* overflow: hidden; */
    position: relative;
    text-align: center;

`;


export const StyledBanner = styled.section`
    padding-top: 15vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    width: 100%;

    h1 {
        margin: 3rem auto;
        font-size: 5rem;
    }

    li {
        list-style: none;
        font-size: 2rem;

        :not(:first-child){
            margin-top: 1rem;
        }
    }
`;

export const BackgroundImage = styled.div`
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%) scale(2);
    width: 50%;
    background: url(${pizza}) no-repeat center ;
    /* transform: scale(2) translateX(20vw); */
    /* animation: ${rotate} 1s linear ;
    animation-fill-mode: forwards; */

    :after {
    content: "";
    display: block;
    padding-bottom: 100%;
}

`;
