import React from 'react';
import { StyledItem, ImageContainer, Image } from './StyledMenu';
import { useSelector, useDispatch } from 'react-redux';

const Item = ({ product }) => {
    const authInfo = useSelector(getName);
    const dispatch = useDispatch();
    const { photo, name, description, price, id } = product;
    return (
        <StyledItem>
            <ImageContainer >
                <Image image={photo} />
            </ImageContainer>
            <h5>{name}</h5>
            <p>{description}</p>
            <p>{`${price} VND`}</p>
            <button>ADD TO CART</button>
        </StyledItem>
    )
}

export default Item;
