import styled, { keyframes } from 'styled-components';
import pizza from "../../assets/pizza.png";

const rotate = keyframes`
  from {

  }
  to {
    transform: scale(1) translateX(-50vw);
  }
`;

export const StyledHome = styled.div`
    position: relative;
    padding-top: 8vh;
    height: 100vh;
    overflow: hidden;
    display: flex;
    align-items: center;
    text-align: center;
`;


export const StyledBanner = styled.section`
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    width: 100%;

    h1 {
        margin: 3rem auto;
        font-size: 5rem;
    }

    li {
        list-style: none;
        font-size: 2rem;

        :not(:first-child){
            margin-top: 1rem;
        }
    }
`;

export const BackgroundImage = styled.div`
    height: 100%;
    width: 100vh;
    background: url(${pizza}) no-repeat center ;
    transform: scale(1.7);
    animation: ${rotate} 1s linear ;
    animation-fill-mode: forwards;

`;
