import React from 'react'
import { StyledCart, StyledInfo, TableHeading, CheckoutForm } from './StyledCart'
import Product from './Product';
import OrderCheckout from './OrderCheckout'
import { useSelector, useDispatch } from 'react-redux';
import Alert from '../sharecomponents/Alert';
import { useFormik } from 'formik';
import { resetMsg } from '../../redux/auth/AuthSlice';
import { checkout } from '../../redux/cart/CartSlice'

const initialValues = {
    address: '',
};

const getAuthenticated = (state) => {
    return state.auth.authenticated;
};

const getMsg = (state) => {
    return state.cart.msg
};

const getCart = (state) => state.cart

function Cart() {


    const cart = useSelector(getCart);
    const keys = Object.keys(cart.products);
    const { items, total } = cart

    const message = useSelector(getMsg);

    const isAuthenticated = useSelector(getAuthenticated);

    const dispatch = useDispatch();

    const formik = useFormik({
        initialValues,
        onSubmit: async (values, { setSubmitting, resetForm }) => {

            setSubmitting(true);
            try {
                dispatch(resetMsg());
                await dispatch(checkout({ values }));
            }
            catch (error) {

            }
            setSubmitting(false);
        },
        validate: (values) => {
            const errors = {};
            const { address } = values;

            if (!address) {
                errors.address = 'Required';
            }
            return errors;

        }
    });
    const { errors, values, handleChange, handleSubmit, isSubmitting } = formik;
    const { address } = values;

    // const makeAlert = React.useCallback(() => {

    //     if (message) {
    //         if (isSubmitting === true) {
    //             alert(message);
    //         }
    //     }
    // }, [message, isSubmitting]);


    return (
        <StyledCart>
            <StyledInfo>
                <TableHeading>
                    <p>Product</p>
                    <p>Price</p>
                    <p>Quantity</p>
                    <p>Total</p>
                </TableHeading>
                {keys.map((productId, index) => {
                    return <Product key={index} product={cart.products[productId]} productId={productId} />
                })}
            </StyledInfo>
            <OrderCheckout data={{ items, total }} />

            <CheckoutForm onSubmit={handleSubmit}>
                <Alert />
                <form action="#">
                    <label htmlFor="address">Address for shipping</label>
                    <input type="text" id="address" name="address" handleChange={handleChange} value={address} />
                    {errors.address ? <div>{errors.address}</div> : null}
                    <button></button>
                </form>
            </CheckoutForm>
        </StyledCart>
    )
}

export default Cart
