import React from 'react';
import styled from 'styled-components';
import logo from '../../../logo.svg';
import sprite from '../../../icons/icomoon/fonts/icomoon.svg';

const StyledHeader = styled.header`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    padding: 3rem 10%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: ${props => props.theme.colors.darkGrey};
    align-items: center;
    font-size: 3rem;

    a, li, button {
        font-size: 1.6rem;
        color: ${props => props.theme.colors.Grey};
        text-decoration: none;

    }
`;

const HeaderNav = styled.div`
    background-color: #fff;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
`;

const HeaderEnd = styled.div`
    background-color: #fff;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const Header = () => {
    return (
        <StyledHeader>
            <HeaderNav> <ul>
                <li><a href="#">Home</a></li>
                <li><a href="#">Promotion</a></li>
                <li><a href="#">Menu</a></li>
                <li><a href="#">Cart</a></li>
            </ul>
            </HeaderNav>
            <HeaderEnd>
                <svg>
                    <use href={logo} />
                </svg>
            </HeaderEnd>


        </StyledHeader>
    )
}

export default Header;
